def validate(test_idx, inverted_list):
    CORRECT = [
        "dcba",
        "aeioubcdfghlmnpqrstvz",
        "xfyRNOz39UwFFc9EAvDScMEy9S6AbB1MtqeVI4RtNQKhWw5NC80LAu6DrJ3xag8F5F1HQXTp3Im5x3VkTcEH8kjM4e8DTh80axEj",
        ""
    ]

    assert isinstance(test_idx, int)
    assert test_idx >= 0
    assert test_idx < len(CORRECT)

    return inverted_list == CORRECT[test_idx]